import React, { useContext, useState } from "react";
import { useForm } from "../../shared/hooks/form-hook";
import Inputs from "../../shared/Components/FormElements/Input";
import { AuthContext } from "../../shared/context/auth-context";
import {VALIDATOR_EMAIL,VALIDATOR_MINLENGTH} from "../../shared/util/validators";

const Auth = () => {
  const auth = useContext(AuthContext);
  const [isloggedin, setisloggedin] = useState(true);

  const [formState, inputHandler, setFormData] = useForm(
    {
      email: {
        value: "",
        isValid: false,
      },
      password: {
        value: "",
        isValid: false,
      },
      name: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const placeUpdateSubmitHandler = event => {
    event.preventDefault();
    console.log(formState.inputs);
    // if(isloggedin){

    // }
    // else{
    //   try{
    //     const response = await fetch('http://localhost:4000/api/users/signup',{
    //       method:'POST',
    //       headers:{
    //         'Content-Type':'application/json'
    //       },
    //       body:JSON.stringify({
    //         name:formState.inputs.name.value,
    //         email:formState.inputs.name.value,
    //         password:formState.inputs.name.value
    //       })
    //     });

    //     const responseData = await response.json();
    //     console.log(responseData);
    //   }
    //   catch(err){
    //     console.log(err);
    //   }

    // }    
    auth.login();
  };



  const switchLoggedInModeHandler = () => {
    // if (!isloggedin) {
    //   setFormData(
    //     {
    //       ...formState.inputs,  
    //       name: undefined,
    //     },
    //     formState.inputs.email.isValid && formState.inputs.password.isValid
    //   );
    // } else {
    //   setFormData(
    //     {
    //       ...formState.inputs,
    //       name: {
    //         value: '',
    //         isValid: false,
    //       },
    //     },
    //     false
    //   );
    // }
    setisloggedin(prevMode => !prevMode);
  };

  return (
    <React.Fragment>
      <form className="place-form" onSubmit={placeUpdateSubmitHandler}>
        {!isloggedin && (
          <Inputs
            id="name"
            inputfieldtype="input"
            type="text"
            label="Name"
            errorText="Please enter a valid Name"
            onInput={inputHandler}
            value={formState.inputs.name.value}
            valid={formState.inputs.name.isValid}          
          />         
        )}
        <Inputs
          id="email"
          inputfieldtype="input"
          type="email"
          label="E-mail"
          errorText="Please enter a valid Email"
          onInput={inputHandler}
          value={formState.inputs.email.value}
          valid={formState.inputs.email.isValid}
        />
      <Inputs
        id="password"
        inputfieldtype="input"
        type="password"
        label="Password"
        errorText="Please enter a password"
        onInput={inputHandler}
        value={formState.inputs.password.value}
        valid={formState.inputs.password.isValid}
      />

        <button type="submit" disabled={!formState.isValid}>{isloggedin ? "Login" : "Register"}</button>
        <button onClick={switchLoggedInModeHandler}>
          {isloggedin ? "Register" : "Login"}
        </button>
      </form>
    </React.Fragment>
  );
};

export default Auth;




