import { useState,useCallback,useRef, useEffect} from "react";

export const useHttpClient = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();

  const activeHttpRequests = useRef([]);

  const sendRequest = useCallback(async (url, method = "GET", body = null, header = {}) => {
    setIsLoading(true);
    const httpAbortCtrl = new AbortController();
    activeHttpRequests.current.push(httpAbortCtrl);
    try {
      const response = await fetch(url, {
        method,
        body,
        headers,
        signal:httpAbortCtrl.signal
      });

      const responseData = await response.json();

      activeHttpRequests.current = activeHttpRequests.current.filter(reqCtrl => reqCtrl !== httpAbortCtrl);

      // This will check all errors if error exist for 400 and 500 it will catch which is not genrally catched.
      if (!response.ok) {
        throw new Error(responseData.message);
      }
      setIsLoading(false);
      return responseData;
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
      throw err;
    }

  },[]);

  const clearError = () => {
    setError(null);
  };

  useEffect(()=>{
    return () => {
        activeHttpRequests.current.forEach(abortCtrl => abortCtrl.abortCtrl());
    };
  },[]);

  return {isLoading,error,sendRequest,clearError};
};
