import React from "react";

import  ReactDOM  from "react-dom";
import './Modal.css';
import BackDrop from "./Navigation/BackDrop";
import {CSSTransition} from 'react-transition-group';

const ModalOverlay = props =>{
    const content = (
        <div className={`modal ${props.className}`} style={props.style}>
            <header className={`modal__header ${props.headerClass}`}>
                <h2>{props.header}</h2>
            </header>
            <form onSubmit={props.onSubmit ? props.onSubmit : event=>event.preventDefault()}>
                <div className={`modal__content ${props.contentClass}`}>
                    {props.children}
                </div>
                <footer className={`modal__footor ${props.footerClass}`}>
                    {props.footer}
                </footer>
            </form>
        </div>
    );
    return ReactDOM.createPortal(content,document.getElementById('modal-hook'));
}

const BootModal = props =>{

    return(
    <React.Fragment>    
       <div id="exampleModalToggle"  class="modal d-block" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">
        <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h3 id="myModalLabel">Modal header</h3>
                <button type="button" class="close" data-dismiss="modal" aria-hidden="true">×</button>
            </div>
            <div class="modal-body">
                <p>One fine body…</p>
            </div>
            <div class="modal-footer">
            <button class="btn btn-primary" data-bs-target="#exampleModalToggle" data-bs-toggle="modal">Close</button>
            </div>
        </div>
    </div>
</div>      

    </React.Fragment>   
    );
}

const Modal = props => {
    return <React.Fragment>
        {/* {props.show && <BackDrop onClick={props.onCancel}/>}
        <CSSTransition in={props.show} mountOnEnter unmountOnExit timeout={200} classNames="modal">
            <ModalOverlay {...props} />
        </CSSTransition> */}
        {props.show && <BootModal />}
    </React.Fragment>
};

export default Modal;
