import React , {useRef} from 'react';

import './ImageUpload.css';

const ImageUpload = props => {
    const filePickerRef = useRef();

    const pickHandler = event => {
        console.log(event.target);
    }

    const pickImageHandler = () => {
        filePickerRef.current.click();
    };

    return(
        <div className='form-control'>
            <input id={props.id} ref={filePickerRef} style={{display:'none'}} type = "file" accept=".jpg,.png,.jpeg" onChange={pickHandler}/>

            <div className={`image-upload ${props.center && 'center'}`}>
                <div className='image-upload_preview'><img src='' alt='Preview' /></div>
                <button onClick={pickImageHandler}>Upload Image</button>
            </div>
        </div>
    )
};

export default ImageUpload;
