import React, { useReducer } from "react";

import Inputs from "../../shared/Components/FormElements/Input";
import './NewPlace.css';

const formReducer = (state, action) => {
  switch (action.type) {
    case 'INPUT_CHANGE':
      let formIsValid = true;
      for (const inputId in state.inputs) {
        if (inputId === action.inputId) {
          formIsValid = formIsValid && action.isValid;
        } else {
          formIsValid = formIsValid && state.inputs[inputId].isValid;
        }
      }
      return {
        ...state,
        inputs: {
          ...state.inputs,
          [action.inputId]: { value: action.value, isValid: action.isValid }
        },
        isValid: formIsValid
      };
    default:
      return state;
  }
};

const NewPlace = props => {
  const [formState, dispatch] = useReducer(formReducer, {
    inputs: {
      title: {
        value: '',
        isValid: false
      },
      description: {
        value: '',
        isValid: false
      }
    },
    isValid: false
  });

  const inputHandler = useCallback((id, value, isValid) => {
    dispatch({
      type: 'INPUT_CHANGE',
      value: value,
      isValid: isValid,
      inputId: id
    });
  }, []);

  const formSubmitHandler = event => {
    event.preventDefault();
  };
    return(
        <form className="place-form" onSubmit={formSubmitHandler}>
          <Inputs id="title" inputfieldtype="input" label="Title" type="text" errorText="Please enter a valid title" onInput={inputHandler}/>
          <Inputs id="description" label="Description" type="text" errorText="Please enter a valid description" onInput={inputHandler}/>
          <Inputs id="address" label="Address" type="text" errorText="Please enter a valid description" onInput={inputHandler}/>
          <button type="submit">Add Place</button>
        </form>

    );
}

export default NewPlace;
