import { Button } from "@material-ui/core";
import Inputs from "../../shared/Components/FormElements/Input";
import React ,{useEffect} from "react";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import { useForm } from "../../shared/hooks/form-hook";

import "../Components/PlaceList.css";

const Dummy_places = [
  {
    id: "p1",
    title: "Empire state Building",
    description: "Tallest Building",
    imageUrl:
      "https://www.kitano.com/resourcefiles/snippet-main-img/empire-state-building-in-new-york-top.jpg?version=8242023115716",
    address: "20 W 34th St., New York, NY 10001",
    location:
      "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3022.617356433533!2d-73.98823932426755!3d40.74844453538418!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c259a9b3117469%3A0xd134e199a405a163!2sEmpire%20State%20Building!5e0!3m2!1sen!2sin!4v1701432335622!5m2!1sen!2sin",
    creatorId: "u1",
  },
  {
    id: "p2",
    title: "Empire state Building",
    description: "Tallest Building",
    imageUrl:
      "https://www.kitano.com/resourcefiles/snippet-main-img/empire-state-building-in-new-york-top.jpg?version=8242023115716",
    address: "20 W 34th St., New York, NY 10001",
    location:
      "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3022.617356433533!2d-73.98823932426755!3d40.74844453538418!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c259a9b3117469%3A0xd134e199a405a163!2sEmpire%20State%20Building!5e0!3m2!1sen!2sin!4v1701432335622!5m2!1sen!2sin",
    creatorId: "u2",
  },
];

const UpdatePlace = () => {
  const placeId = useParams().placeId;


  const [formState, inputHandler,setFormData] = useForm(
    {
      title: {
        value: '',
        isValid: false,
      },
      description: {
        value: '',
        isValid: false,
      },
    },
    false
  );

  const IdentifierPlace = Dummy_places.find((p) => p.id === placeId);

  useEffect(()=>{
    setFormData({
      title: {
        value: IdentifierPlace.title,
        isValid: true,
      },
      description: {
        value: IdentifierPlace.title,
        isValid: true,
      },
    },true);
  },[setFormData,IdentifierPlace])


  const placeUpdateSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  if (!IdentifierPlace) {
    return (
      <div className="center">
        <h2>Place Not Found</h2>
      </div>
    );
  }

  if(!formState.inputs.title.value){
    return (
      <div className="center">
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <form className="place-form" onSubmit={placeUpdateSubmitHandler}>
      <Inputs
        id="Title"
        inputfieldtype="input"
        type="text"
        label="Title"
        errorText="Please enter a valid title"
        onInput={inputHandler}
        value={formState.inputs.title.value}
        valid={formState.inputs.title.isValid}
      />
      <Inputs
        id="Description"
        inputfieldtype="textarea"
        label="Description"
        errorText="Please enter a valid Description"
        onInput={inputHandler}
        value={formState.inputs.description.value}
        valid={formState.inputs.description.isValid}
      />
      <Button type="submit">Update Place</Button>
    </form>
  );
};

export default UpdatePlace;
