import React, { useState } from "react";
import { useForm } from "../../shared/hooks/form-hook";
import Inputs from "../../shared/Components/FormElements/Input";

const Auth = () => {
  const [formState, inputHandler] = useForm(
    {
      email: {
        value: "",
        isValid: false,
      },
      password: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const placeUpdateSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  const [isloggedin, setisloggedin] = useState(true);

  const switchLoggedInModeHandler = () => {
    setisloggedin((prevMode) => !prevMode);
  };

  return (
    <React.Fragment>
      <form className="place-form" onSubmit={placeUpdateSubmitHandler}>
        {/* {!isloggedin ? : (
          <>
          <h2>Login</h2>
          <Inputs
            id="email"
            inputfieldtype="input"
            type="email"
            label="E-mail"
            errorText="Please enter a valid Email"
            onInput={inputHandler}
            value={formState.inputs.email.value}
            valid={formState.inputs.email.isValid}
          />
          <Inputs
          id="Password"
          inputfieldtype="input"
          type="password"
          label="Password"
          errorText="Please enter a password"
          onInput={inputHandler}
          value={formState.inputs.password.value}
          valid={formState.inputs.password.isValid}
        />
          </>  
        )} */}
        {!isloggedin ? (<><h2>Login</h2>
          <Inputs
            id="email"
            inputfieldtype="input"
            type="email"
            label="E-mail"
            errorText="Please enter a valid Email"
            onInput={inputHandler}
            value={formState.inputs.email.value}
            valid={formState.inputs.email.isValid}
          />
          <Inputs
          id="Password"
          inputfieldtype="input"
          type="password"
          label="Password"
          errorText="Please enter a password"
          onInput={inputHandler}
          value={formState.inputs.password.value}
          valid={formState.inputs.password.isValid}
        /></>) : (<><h2>Registration</h2>
        <Inputs
          id="email"
          inputfieldtype="input"
          type="email"
          label="E-mail"
          errorText="Please enter a valid Email"
          onInput={inputHandler}
          value={formState.inputs.email.value}
          valid={formState.inputs.email.isValid}
        />
        <Inputs
          id="Password"
          inputfieldtype="input"
          type="password"
          label="Password"
          errorText="Please enter a password"
          onInput={inputHandler}
          value={formState.inputs.password.value}
          valid={formState.inputs.password.isValid}
        /></>)}
        {/* <h2>Registration</h2>
        <Inputs
          id="email"
          inputfieldtype="input"
          type="email"
          label="E-mail"
          errorText="Please enter a valid Email"
          onInput={inputHandler}
          value={formState.inputs.email.value}
          valid={formState.inputs.email.isValid}
        />
        <Inputs
          id="Password"
          inputfieldtype="input"
          type="password"
          label="Password"
          errorText="Please enter a password"
          onInput={inputHandler}
          value={formState.inputs.password.value}
          valid={formState.inputs.password.isValid}
        /> */}
        <button type="submit">{!isloggedin ? "Login" : "Register"}</button>
        <button onClick={switchLoggedInModeHandler}>
          {isloggedin ? "Switch to Login" : "Switch to Register"}
        </button>
      </form>
    </React.Fragment>
  );
};

export default Auth;
